import * as types from "../../actionTypes";
import VideosApi from "../../../api/VideosApi";

let actionsFunction = {

  //CRUD METHODS

  // Delete videos
  deleteVideos: function(id) {
    return function(dispatch) {
      return VideosApi
        .deleteVideos(id)
        .then(videos => {
          dispatch(actionsFunction.deleteVideosSuccess(videos));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteVideosSuccess: function(videos) {
    return { type: types.DELETE_VIDEOS_SUCCESS, payload: videos };
  },

  // Load  list
  loadVideosList: function() {
    return function(dispatch) {
      return VideosApi
        .getVideosList()
        .then(list => {
          dispatch(actionsFunction.loadVideosListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadVideosListSuccess: function(list) {
    return { type: types.LIST_VIDEOS_SUCCESS, payload: list };
  },


};

export default actionsFunction;
